function createSilverlightEx()
{  
    Silverlight.createObjectEx({
        source: '${ProjectName}.xaml',
        parentElement:parentElement,
        id:'${ProjectName}',
        properties:{
            width:'1000',
            height:'600',
            inplaceInstallPrompt:false,
            background:'white',
            isWindowless:'false',
            framerate:'24',
            version:'2.0'},
        events:{
            onError:null,
            onLoad:null},
        initParams:null,
        context:null});
}
